INSERT INTO `service_faqs` (`id`, `service_id`, `question`, `answer`, `created_at`, `updated_at`) VALUES
(7, 3, '{\"en\":\"How often should I service my window air conditioner?\",\"ar\":\"                   \"}', '{\"en\":\"It is recommended to service your window air conditioner at least once a year to maintain its efficiency and prolong its lifespan.\",\"ar\":\"                              .\"}', '2024-06-14 16:12:40', '2025-02-14 04:51:46'),
(8, 3, '{\"en\":\"What should I do if my air conditioner is not cooling properly?\",\"ar\":\"                     \"}', '{\"en\":\"Check the filter and clean it if necessary. If the problem persists, contact our technicians for a thorough inspection and repair.\",\"ar\":\"         .                 .\"}', '2024-06-14 16:12:40', '2025-02-14 04:51:46'),
(9, 3, '{\"en\":\"Can you repair all brands of window air conditioners?\",\"ar\":\"             \"}', '{\"en\":\"Yes, our technicians are trained to repair all major brands of window air conditioners.\",\"ar\":\"                          .\"}', '2024-06-14 16:12:40', '2025-02-14 04:51:46'),
(10, 4, '{\"en\":\"What cleaning products do you use?\"}', '{\"en\":\"We use eco-friendly and non-toxic cleaning products to ensure the safety and health of your employees.\"}', '2024-06-14 16:16:21', '2024-06-14 16:16:21'),
(11, 4, '{\"en\":\"How often should my office be cleaned?\"}', '{\"en\":\"The frequency of cleaning depends on the size of your office and the number of employees. We offer daily, weekly, and monthly cleaning services to suit your needs.\"}', '2024-06-14 16:16:21', '2024-06-14 16:16:21'),
(12, 4, '{\"en\":\"Do you provide cleaning services after office hours?\"}', '{\"en\":\"Yes, we offer flexible cleaning schedules, including after-hours and weekend services, to minimize disruption to your business operations.\"}', '2024-06-14 16:16:21', '2024-06-14 16:16:21'),
(13, 5, '{\"en\":\"How long does it take to make custom furniture?\"}', '{\"en\":\"The time required to make custom furniture depends on the complexity of the design and the materials used. We will provide a timeline during the initial consultation.\"}', '2024-06-14 16:19:54', '2024-06-14 16:19:54'),
(14, 5, '{\"en\":\"Can you work with my design ideas?\"}', '{\"en\":\"Yes, we collaborate with our clients to incorporate their design ideas and preferences into the final product.\"}', '2024-06-14 16:19:54', '2024-06-14 16:19:54'),
(15, 5, '{\"en\":\"Do you offer delivery and installation services?\"}', '{\"en\":\"Yes, we offer delivery and installation services to ensure your custom furniture is set up correctly and safely.\"}', '2024-06-14 16:19:54', '2024-06-14 16:19:54'),
(16, 6, '{\"en\":\"Do you offer emergency electrical services?\",\"ar\":\"       \"}', '{\"en\":\"Yes, we offer 24/7 emergency electrical services to address urgent issues and ensure your safety.\",\"ar\":\"                           .\"}', '2024-06-14 16:24:23', '2025-02-14 06:03:13'),
(17, 6, '{\"en\":\"How often should I have my electrical system inspected?\",\"ar\":\"             \"}', '{\"en\":\"It is recommended to have your electrical system inspected every 3-5 years, or immediately if you notice any signs of electrical problems.\",\"ar\":\"              3-5                      .\"}', '2024-06-14 16:24:23', '2025-02-14 06:03:13'),
(18, 6, '{\"en\":\"Can you handle both small and large electrical projects?\",\"ar\":\"              \"}', '{\"en\":\"Yes, our electricians are equipped to handle projects of all sizes, from minor repairs to large-scale installations.\",\"ar\":\"                             .\"}', '2024-06-14 16:24:23', '2025-02-14 06:03:13'),
(19, 7, '{\"en\":\"How long does it take to paint a room?\"}', '{\"en\":\"The time required to paint a room depends on its size and the condition of the walls. On average, it takes about 1-2 days per room.\"}', '2024-06-14 16:29:54', '2024-06-14 16:29:54'),
(20, 7, '{\"en\":\"Can you help me choose the right paint colors?\"}', '{\"en\":\"Yes, our painters can provide color consultation services to help you choose the perfect colors for your home.\"}', '2024-06-14 16:29:54', '2024-06-14 16:29:54'),
(21, 7, '{\"en\":\"Do you offer any warranties on your painting services?\"}', '{\"en\":\"Yes, we offer a satisfaction guarantee and warranty on all our painting services to ensure your complete peace of mind.\"}', '2024-06-14 16:29:54', '2024-06-14 16:29:54'),
(22, 8, '{\"en\":\"What techniques are used in stress relief therapy?\"}', '{\"en\":\"Our therapists use a combination of massage, aromatherapy, and guided relaxation techniques.\"}', '2024-06-14 16:42:06', '2024-06-14 16:42:06'),
(23, 8, '{\"en\":\"How long does each session last?\"}', '{\"en\":\"Each session typically lasts between 60 to 90 minutes.\"}', '2024-06-14 16:42:06', '2024-06-14 16:42:06'),
(24, 8, '{\"en\":\"How often should I schedule therapy sessions?\"}', '{\"en\":\"We recommend scheduling sessions at least once a week for optimal results.\"}', '2024-06-14 16:42:06', '2024-06-14 16:42:06'),
(25, 9, '{\"en\":\"What conditions can pain relief therapy help with?\"}', '{\"en\":\"Pain relief therapy can help with conditions such as back pain, neck pain, arthritis, and sports injuries.\"}', '2024-06-14 16:46:08', '2024-06-14 16:46:08'),
(26, 9, '{\"en\":\"Do I need a doctor\'s referral for pain relief therapy?\"}', '{\"en\":\"No, you do not need a doctor\'s referral to book a session.\"}', '2024-06-14 16:46:08', '2024-06-14 16:46:08'),
(27, 9, '{\"en\":\"How many sessions will I need to see improvement?\"}', '{\"en\":\"The number of sessions required varies based on the individual\'s condition and response to therapy.\"}', '2024-06-14 16:46:08', '2024-06-14 16:46:08'),
(28, 10, '{\"en\":\"What types of natural products are used?\"}', '{\"en\":\"We use products made from natural ingredients like aloe vera, coconut oil, and essential oils.\"}', '2024-06-14 16:51:34', '2024-06-14 16:51:34'),
(29, 10, '{\"en\":\"Are the treatments suitable for all skin types?\"}', '{\"en\":\"Yes, our treatments are customized to suit all skin types.\"}', '2024-06-14 16:51:34', '2024-06-14 16:51:34'),
(30, 10, '{\"en\":\"How often should I get a skincare treatment?\"}', '{\"en\":\"We recommend getting a skincare treatment every 4-6 weeks for best results.\"}', '2024-06-14 16:51:34', '2024-06-14 16:51:34'),
(31, 11, '{\"en\":\"How long does the cleaning process take?\"}', '{\"en\":\"The cleaning process typically takes 1-2 hours per sofa or carpet, depending on the size and condition.\"}', '2024-06-14 16:55:47', '2024-06-14 16:55:47'),
(32, 11, '{\"en\":\"Are the cleaning products safe for children and pets?\"}', '{\"en\":\"Yes, we use eco-friendly and non-toxic cleaning products.\"}', '2024-06-14 16:55:47', '2024-06-14 16:55:47'),
(33, 11, '{\"en\":\"How often should I have my sofa and carpet cleaned?\"}', '{\"en\":\"It is recommended to clean your sofa and carpet every 6-12 months.\"}', '2024-06-14 16:55:47', '2024-06-14 16:55:47'),
(34, 12, '{\"en\":\"What areas are covered in the cleaning service?\",\"ar\":\"      \"}', '{\"en\":\"Our cleaning service covers all surfaces, including sinks, countertops, floors, and appliances.\",\"ar\":\"            .\"}', '2024-06-14 16:59:04', '2025-04-07 12:14:44'),
(35, 12, '{\"en\":\"Do you provide your own cleaning supplies?\",\"ar\":\"                  .\"}', '{\"en\":\"Yes, we provide all the necessary cleaning supplies and equipment.\",\"ar\":\"       .\"}', '2024-06-14 16:59:04', '2025-04-07 12:14:44'),
(36, 12, '{\"en\":\"Can I book a one-time cleaning service?\",\"ar\":\"         .\"}', '{\"en\":\"Yes, we offer both one-time and regular cleaning services.\",\"ar\":\"           .\"}', '2024-06-14 16:59:04', '2025-04-07 12:14:44'),
(37, 13, '{\"en\":\"What can I expect during an Ayurvedic Massage?\",\"ar\":\"       \"}', '{\"en\":\"During an Ayurvedic Massage, you can expect a calming and nurturing experience. The therapist will use warm Ayurvedic oils and rhythmic strokes to promote relaxation and balance in the body.\",\"ar\":\"         .             .\"}', '2024-06-15 08:53:50', '2025-04-07 12:10:48'),
(38, 13, '{\"en\":\"Is Ayurvedic Massage suitable for everyone?\",\"ar\":\"    \"}', '{\"en\":\"Ayurvedic Massage is generally safe for most people. However, individuals with certain health conditions or pregnant women should consult with their healthcare provider before receiving this treatment.\",\"ar\":\"       .                        .\"}', '2024-06-15 08:53:50', '2025-04-07 12:10:48'),
(39, 13, '{\"en\":\"How often should I get an Ayurvedic Massage?\",\"ar\":\"       \"}', '{\"en\":\"The frequency of Ayurvedic Massage depends on your individual needs and health goals. Some people benefit from weekly sessions, while others may choose to receive massages less frequently. It\'s best to consult with an Ayurvedic practitioner to determine the right frequency for you.\",\"ar\":\"         .               .         .\"}', '2024-06-15 08:53:50', '2025-04-07 12:10:48'),
(40, 14, '{\"en\":\"How does Ayurvedic Pain Relief Head, Neck & Shoulder Massage differ from a regular massage?\",\"ar\":\"           \"}', '{\"en\":\"Ayurvedic Pain Relief Head, Neck & Shoulder Massage is tailored to address specific areas of discomfort and tension. The techniques used are designed to relieve pain, reduce inflammation, and improve mobility in these areas.\",\"ar\":\"            .             .\"}', '2024-06-15 09:02:45', '2025-04-07 11:39:17'),
(41, 14, '{\"en\":\"Is Ayurvedic Pain Relief Head, Neck & Shoulder Massage suitable for everyone?\",\"ar\":\"         \"}', '{\"en\":\"While Ayurvedic Massage is generally safe for most people, individuals with certain health conditions or injuries should consult with their healthcare provider before receiving this treatment.\",\"ar\":\"                                 .\"}', '2024-06-15 09:02:45', '2025-04-07 11:39:17'),
(42, 14, '{\"en\":\"How often should I get an Ayurvedic Pain Relief Head, Neck & Shoulder Massage?\",\"ar\":\"             \"}', '{\"en\":\"The frequency of this massage depends on your individual needs and the severity of your symptoms. Some people benefit from regular sessions, while others may find relief with less frequent treatments. It\'s best to consult with an Ayurvedic practitioner to determine the right frequency for you.\",\"ar\":\"           .                .         .\"}', '2024-06-15 09:02:45', '2025-04-07 11:39:17'),
(43, 15, '{\"en\":\"How often should I have my chimney swept?\",\"ar\":\"      \"}', '{\"en\":\"Once a year for fireplaces used regularly and twice a year for wood-burning stoves.\",\"ar\":\"            .\"}', '2024-06-15 09:38:14', '2025-04-07 10:59:30'),
(44, 15, '{\"en\":\"What does the cleaning process involve?\",\"ar\":\"     \"}', '{\"en\":\"We use a combination of brushes, vacuums, and rods to remove buildup from the flue, firebox, and damper.\",\"ar\":\"             .\"}', '2024-06-15 09:38:14', '2025-04-07 10:59:30'),
(45, 15, '{\"en\":\"Do you offer same-day appointments?\",\"ar\":\"     \"}', '{\"en\":\"Same-day appointments may be available depending on scheduling. Please call to confirm.\",\"ar\":\"        .    .\"}', '2024-06-15 09:38:14', '2025-04-07 10:59:30'),
